import React, { useState, useRef, useEffect } from "react";
import omagpaGif from "./omagpa.gif";

function AudioPlayer() {
  const AUDIO_FILE_DEFAULT = "./26.7.19.mp3";
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [currentTrack, setCurrentTrack] = useState(AUDIO_FILE_DEFAULT);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const audioRef = useRef(null);

  const audio_files = [
    "./01.10.21.mp3",
    "./7-6-19.mp3",
    "./7-17-19.mp3",
    "./7-26-19.mp3",
  ];

  const handlePlay = () => {
    audioRef.current.play();
    setIsPlaying(true);
  };

  const handlePause = () => {
    audioRef.current.pause();
    setIsPlaying(false);
  };

  const handlePlayPause = () => {
    if (isPlaying) {
      handlePause();
    } else {
      handlePlay();
    }
  };

  const handleNext = () => {
    const nextTrackIndex = (currentTrackIndex + 1) % audio_files.length;
    setCurrentTrackIndex(nextTrackIndex);
    setCurrentTrack(audio_files[nextTrackIndex]);
    audioRef.current.load();
    audioRef.current.addEventListener(
      "canplay",
      () => {
        audioRef.current.play();
        setIsPlaying(true);
      },
      { once: true }
    );
  };

  const handleTimeUpdate = () => {
    setCurrentTime(audioRef.current.currentTime);
    setDuration(audioRef.current.duration);
  };

  const handleSeek = (e) => {
    audioRef.current.currentTime = e.target.value;
    setCurrentTime(e.target.value);
  };

  function formatDuration(durationSeconds) {
    const minutes = Math.floor(durationSeconds / 60);
    const seconds = Math.floor(durationSeconds % 60);
    const formattedSeconds = seconds.toString().padStart(2, "0");
    return `${minutes}:${formattedSeconds}`;
  }

  useEffect(() => {
    audioRef.current.addEventListener("timeupdate", handleTimeUpdate);
    return () => {
      audioRef.current.removeEventListener("timeupdate", handleTimeUpdate);
    };
  }, []);

  useEffect(() => {
    audioRef.current.addEventListener("timeupdate", handleTimeUpdate);
    audioRef.current.addEventListener("loadedmetadata", () => {
      setDuration(audioRef.current.duration);
    });
    return () => {
      audioRef.current.removeEventListener("timeupdate", handleTimeUpdate);
      audioRef.current.removeEventListener("loadedmetadata", () => {
        setDuration(audioRef.current.duration);
      });
    };
  }, []);

  return (
    <>
      <div className="player-card">
        <img src={omagpaGif} />

        <input
          type="range"
          min="0"
          max={duration}
          value={currentTime}
          onChange={handleSeek}
        />
        <audio ref={audioRef} src={currentTrack} />
        <div className="track-duration">
          <p>{formatDuration(currentTime)}</p>
          <p>{formatDuration(duration)}</p>
        </div>
        <div className="flex flex2">
          <button className="flex-items">
            <span className="back-forth"></span>
          </button>

          <button className="flex-items">
            <span onClick={handlePlayPause} className="play-pause">
              {isPlaying ? "⏸" : "▶"}
            </span>
          </button>

          <button className="flex-items">
            <span onClick={handleNext} className="back-forth">
              ⏭
            </span>
          </button>
        </div>
      </div>
    </>
  );
}

export default AudioPlayer;

